module O_GaussianIntegralsVec

   ! Import necessary modules.
   use O_Kinds

   ! Make sure that no funny variables are defined.
   implicit none

   ! Define the access level.
   public

   !!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!
   ! Begin list of module subroutines.!
   !!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!
   contains

   ! These subroutines are based primarily on the work of Obara-Saika as
   !   published in: Obara S, Saika A., "Efficient recursive computation of
   !   molecular integrals over Cartesian Gaussian functions", The Journal of
   !   Chemical Physics, (1986), Volume 84(7), pages: 3963-3974. Additional
   !   guidance can be found in: Helgaker T, Jørgensen P, Olsen J.,
   !   "Molecular electronic-structure theory. Chichester ; New York: Wiley;
   !   2000. 908 pages; (Primarily chapter 9).
   

   subroutine overlap2CIntgVec(A,B,numAlphaPairs,maxNumAlphaPairs,&
         & orderedAlphaPairs,sh,pc,numSegs,segIndices)

   use O_Kinds
   use O_Constants, only: pi, lAngMomCount

   implicit none

   ! sh(:,16,16): 1,s; 2,x; 3,y; 4,z; 5,xy; 6,xz; 7,yz; 8,xx-yy;
   ! 9,2zz-xx-yy; 10,xyz; 11,xxz-yyz; 12,xxx-3yyx; 13,3xxy-yyy; 
   ! 14,2zzz-3xxz-3yyz; 15,4zzx-xxx-yyx; 16,4zzy-xxy-yyy

   ! pc(:,20,20): 1,s; 2,x; 3,y; 4,z; 5,xx; 6,yy; 7,zz; 8,xy; 9,xz;
   ! 10,yz; 11,xyz; 12,xxy; 13,xxz; 14,yyx; 15,yyz; 16,zzx; 17,zzy
   ! 18,xxx; 19,yyy; 20,zzz

   ! Define the dummy variables passed to this subroutine.
   real (kind=double), dimension (3), intent (in) :: A, B
   integer, intent (in) :: numAlphaPairs
   integer, intent (in) :: maxNumAlphaPairs
   real (kind=double), dimension(2,maxNumAlphaPairs), intent (in) :: &
         & orderedAlphaPairs
   real (kind=double), dimension (maxNumAlphaPairs,16,16), intent (out) :: sh
   real (kind=double), dimension (maxNumAlphaPairs,20,20), intent (inout) :: pc
   integer, dimension(lAngMomCount,lAngMomCount) :: numSegs
   integer, dimension(2,lAngMomCount,lAngMomCount,lAngMomCount), &
         & intent (in) :: segIndices

   ! Define local variables
   integer :: i, j
   real (kind=double) :: sum_d_sqrd
   real (kind=double), dimension(numAlphaPairs,3) :: P, PA, PB
   real (kind=double), dimension(numAlphaPairs) :: inv_zeta, inv_2zeta
   real (kind=double), dimension(numAlphaPairs) :: xi, preFactorOL

   ! Initialize local variables.
   inv_zeta(:numAlphaPairs) = 1.0d0 / (orderedAlphaPairs(1,:numAlphaPairs) + &
         & orderedAlphaPairs(2,:numAlphaPairs))
   inv_2zeta(:numAlphaPairs) = 0.5d0 * inv_zeta(:numAlphaPairs)
   xi(:numAlphaPairs) = orderedAlphaPairs(1,:numAlphaPairs) * &
         & orderedAlphaPairs(2,:numAlphaPairs) * inv_zeta(:numAlphaPairs)
   do i = 1, 3
      P(:numAlphaPairs,i) = (orderedAlphaPairs(1,:numAlphaPairs)*A(i) &
            & + orderedAlphaPairs(2,:numAlphaPairs)*B(i)) &
            & * inv_zeta(:numAlphaPairs)
      PA(:numAlphaPairs,i) = P(:numAlphaPairs,i) - A(i)
      PB(:numAlphaPairs,i) = P(:numAlphaPairs,i) - B(i)
   enddo
   sum_d_sqrd = sum((A(:)-B(:))**2)
   preFactorOL(:numAlphaPairs) = ((pi*inv_zeta(:numAlphaPairs))**1.5d0) &
         & * exp(-xi(:numAlphaPairs)*sum_d_sqrd)

!write(6,*) "numSegs=", numSegs(1,1)
do i = 1, numSegs(1,1)
!write(6,*) "i, segIndices=", i, segIndices(1,i,1,1),segIndices(2,i,1,1)
   do j = segIndices(1,i,1,1),segIndices(2,i,1,1)
   pc(j,1,1) = preFactorOL(j)
!write(6,*) "j ,pc=", j, pc(j,1,1)
   enddo
enddo

do i = 1, numSegs(2,1)
   do j = segIndices(1,i,2,1),segIndices(2,i,2,1)
   pc(j,2,1) = PA(j,1)*preFactorOL(j)
   enddo
enddo

do i = 1, numSegs(2,1)
   do j = segIndices(1,i,2,1),segIndices(2,i,2,1)
   pc(j,3,1) = PA(j,2)*preFactorOL(j)
   enddo
enddo

do i = 1, numSegs(2,1)
   do j = segIndices(1,i,2,1),segIndices(2,i,2,1)
   pc(j,4,1) = PA(j,3)*preFactorOL(j)
   enddo
enddo

do i = 1, numSegs(3,1)
   do j = segIndices(1,i,3,1),segIndices(2,i,3,1)
   pc(j,5,1) = PA(j,1)*pc(j,2,1) + inv_2zeta(j)*(preFactorOL(j))
   enddo
enddo

do i = 1, numSegs(3,1)
   do j = segIndices(1,i,3,1),segIndices(2,i,3,1)
   pc(j,6,1) = PA(j,2)*pc(j,3,1) + inv_2zeta(j)*(preFactorOL(j))
   enddo
enddo

do i = 1, numSegs(3,1)
   do j = segIndices(1,i,3,1),segIndices(2,i,3,1)
   pc(j,7,1) = PA(j,3)*pc(j,4,1) + inv_2zeta(j)*(preFactorOL(j))
   enddo
enddo

do i = 1, numSegs(3,1)
   do j = segIndices(1,i,3,1),segIndices(2,i,3,1)
   pc(j,8,1) = PA(j,2)*pc(j,2,1)
   enddo
enddo

do i = 1, numSegs(3,1)
   do j = segIndices(1,i,3,1),segIndices(2,i,3,1)
   pc(j,9,1) = PA(j,3)*pc(j,2,1)
   enddo
enddo

do i = 1, numSegs(3,1)
   do j = segIndices(1,i,3,1),segIndices(2,i,3,1)
   pc(j,10,1) = PA(j,3)*pc(j,3,1)
   enddo
enddo

do i = 1, numSegs(4,1)
   do j = segIndices(1,i,4,1),segIndices(2,i,4,1)
   pc(j,11,1) = PA(j,3)*pc(j,8,1)
   enddo
enddo

do i = 1, numSegs(4,1)
   do j = segIndices(1,i,4,1),segIndices(2,i,4,1)
   pc(j,12,1) = PA(j,2)*pc(j,5,1)
   enddo
enddo

do i = 1, numSegs(4,1)
   do j = segIndices(1,i,4,1),segIndices(2,i,4,1)
   pc(j,13,1) = PA(j,3)*pc(j,5,1)
   enddo
enddo

do i = 1, numSegs(4,1)
   do j = segIndices(1,i,4,1),segIndices(2,i,4,1)
   pc(j,14,1) = PA(j,1)*pc(j,6,1)
   enddo
enddo

do i = 1, numSegs(4,1)
   do j = segIndices(1,i,4,1),segIndices(2,i,4,1)
   pc(j,15,1) = PA(j,3)*pc(j,6,1)
   enddo
enddo

do i = 1, numSegs(4,1)
   do j = segIndices(1,i,4,1),segIndices(2,i,4,1)
   pc(j,16,1) = PA(j,1)*pc(j,7,1)
   enddo
enddo

do i = 1, numSegs(4,1)
   do j = segIndices(1,i,4,1),segIndices(2,i,4,1)
   pc(j,17,1) = PA(j,2)*pc(j,7,1)
   enddo
enddo

do i = 1, numSegs(4,1)
   do j = segIndices(1,i,4,1),segIndices(2,i,4,1)
   pc(j,18,1) = PA(j,1)*pc(j,5,1) + inv_2zeta(j)*(2*pc(j,2,1))
   enddo
enddo

do i = 1, numSegs(4,1)
   do j = segIndices(1,i,4,1),segIndices(2,i,4,1)
   pc(j,19,1) = PA(j,2)*pc(j,6,1) + inv_2zeta(j)*(2*pc(j,3,1))
   enddo
enddo

do i = 1, numSegs(4,1)
   do j = segIndices(1,i,4,1),segIndices(2,i,4,1)
   pc(j,20,1) = PA(j,3)*pc(j,7,1) + inv_2zeta(j)*(2*pc(j,4,1))
   enddo
enddo

do i = 1, numSegs(1,2)
   do j = segIndices(1,i,1,2),segIndices(2,i,1,2)
   pc(j,1,2) = PB(j,1)*preFactorOL(j)
   enddo
enddo

do i = 1, numSegs(2,2)
   do j = segIndices(1,i,2,2),segIndices(2,i,2,2)
   pc(j,2,2) = PA(j,1)*pc(j,1,2) + inv_2zeta(j)*(preFactorOL(j))
   enddo
enddo

do i = 1, numSegs(2,2)
   do j = segIndices(1,i,2,2),segIndices(2,i,2,2)
   pc(j,3,2) = PA(j,2)*pc(j,1,2)
   enddo
enddo

do i = 1, numSegs(2,2)
   do j = segIndices(1,i,2,2),segIndices(2,i,2,2)
   pc(j,4,2) = PA(j,3)*pc(j,1,2)
   enddo
enddo

do i = 1, numSegs(3,2)
   do j = segIndices(1,i,3,2),segIndices(2,i,3,2)
   pc(j,5,2) = PA(j,1)*pc(j,2,2) + inv_2zeta(j)*(pc(j,1,2) + pc(j,2,1))
   enddo
enddo

do i = 1, numSegs(3,2)
   do j = segIndices(1,i,3,2),segIndices(2,i,3,2)
