   enddo
enddo

do i = 1, numSegs(3,2)
   do j = segIndices(1,i,3,2),segIndices(2,i,3,2)
   pc(j,7,2) = PA(j,3)*pc(j,4,2) + inv_2zeta(j)*(pc(j,1,2))
   enddo
enddo

do i = 1, numSegs(3,2)
   do j = segIndices(1,i,3,2),segIndices(2,i,3,2)
   pc(j,8,2) = PA(j,2)*pc(j,2,2)
   enddo
enddo

do i = 1, numSegs(3,2)
   do j = segIndices(1,i,3,2),segIndices(2,i,3,2)
   pc(j,9,2) = PA(j,3)*pc(j,2,2)
   enddo
enddo

do i = 1, numSegs(3,2)
   do j = segIndices(1,i,3,2),segIndices(2,i,3,2)
   pc(j,10,2) = PA(j,3)*pc(j,3,2)
   enddo
enddo

do i = 1, numSegs(4,2)
   do j = segIndices(1,i,4,2),segIndices(2,i,4,2)
   pc(j,11,2) = PA(j,3)*pc(j,8,2)
   enddo
enddo

do i = 1, numSegs(4,2)
   do j = segIndices(1,i,4,2),segIndices(2,i,4,2)
   pc(j,12,2) = PA(j,2)*pc(j,5,2)
   enddo
enddo

do i = 1, numSegs(4,2)
   do j = segIndices(1,i,4,2),segIndices(2,i,4,2)
   pc(j,13,2) = PA(j,3)*pc(j,5,2)
   enddo
enddo

do i = 1, numSegs(4,2)
   do j = segIndices(1,i,4,2),segIndices(2,i,4,2)
   pc(j,14,2) = PA(j,1)*pc(j,6,2) + inv_2zeta(j)*(pc(j,6,1))
   enddo
enddo

do i = 1, numSegs(4,2)
   do j = segIndices(1,i,4,2),segIndices(2,i,4,2)
   pc(j,15,2) = PA(j,3)*pc(j,6,2)
   enddo
enddo

do i = 1, numSegs(4,2)
   do j = segIndices(1,i,4,2),segIndices(2,i,4,2)
   pc(j,16,2) = PA(j,1)*pc(j,7,2) + inv_2zeta(j)*(pc(j,7,1))
   enddo
enddo

do i = 1, numSegs(4,2)
   do j = segIndices(1,i,4,2),segIndices(2,i,4,2)
   pc(j,17,2) = PA(j,2)*pc(j,7,2)
   enddo
enddo

do i = 1, numSegs(4,2)
   do j = segIndices(1,i,4,2),segIndices(2,i,4,2)
   pc(j,18,2) = PA(j,1)*pc(j,5,2) + inv_2zeta(j)*(2*pc(j,2,2) + pc(j,5,1))
   enddo
enddo

do i = 1, numSegs(4,2)
   do j = segIndices(1,i,4,2),segIndices(2,i,4,2)
   pc(j,19,2) = PA(j,2)*pc(j,6,2) + inv_2zeta(j)*(2*pc(j,3,2))
   enddo
enddo

do i = 1, numSegs(4,2)
   do j = segIndices(1,i,4,2),segIndices(2,i,4,2)
   pc(j,20,2) = PA(j,3)*pc(j,7,2) + inv_2zeta(j)*(2*pc(j,4,2))
   enddo
enddo

do i = 1, numSegs(1,2)
   do j = segIndices(1,i,1,2),segIndices(2,i,1,2)
   pc(j,1,3) = PB(j,2)*preFactorOL(j)
   enddo
enddo

do i = 1, numSegs(2,2)
   do j = segIndices(1,i,2,2),segIndices(2,i,2,2)
   pc(j,2,3) = PA(j,1)*pc(j,1,3)
   enddo
enddo

do i = 1, numSegs(2,2)
   do j = segIndices(1,i,2,2),segIndices(2,i,2,2)
   pc(j,3,3) = PA(j,2)*pc(j,1,3) + inv_2zeta(j)*(preFactorOL(j))
   enddo
enddo

do i = 1, numSegs(2,2)
   do j = segIndices(1,i,2,2),segIndices(2,i,2,2)
   pc(j,4,3) = PA(j,3)*pc(j,1,3)
   enddo
enddo

do i = 1, numSegs(3,2)
   do j = segIndices(1,i,3,2),segIndices(2,i,3,2)
   pc(j,5,3) = PA(j,1)*pc(j,2,3) + inv_2zeta(j)*(pc(j,1,3))
   enddo
enddo

do i = 1, numSegs(3,2)
   do j = segIndices(1,i,3,2),segIndices(2,i,3,2)
   pc(j,6,3) = PA(j,2)*pc(j,3,3) + inv_2zeta(j)*(pc(j,1,3) + pc(j,3,1))
   enddo
