   enddo
enddo

do i = 1, numSegs(3,2)
   do j = segIndices(1,i,3,2),segIndices(2,i,3,2)
   pc(j,8,3) = PA(j,2)*pc(j,2,3) + inv_2zeta(j)*(pc(j,2,1))
   enddo
enddo

do i = 1, numSegs(3,2)
   do j = segIndices(1,i,3,2),segIndices(2,i,3,2)
   pc(j,9,3) = PA(j,3)*pc(j,2,3)
   enddo
enddo

do i = 1, numSegs(3,2)
   do j = segIndices(1,i,3,2),segIndices(2,i,3,2)
   pc(j,10,3) = PA(j,3)*pc(j,3,3)
   enddo
enddo

do i = 1, numSegs(4,2)
   do j = segIndices(1,i,4,2),segIndices(2,i,4,2)
   pc(j,11,3) = PA(j,3)*pc(j,8,3)
   enddo
enddo

do i = 1, numSegs(4,2)
   do j = segIndices(1,i,4,2),segIndices(2,i,4,2)
